mapper.update:
UPDATE tb_song
singer_id = #{singerId}, name = #{name}, album = #{album}, duration = #{duration}, style = #{style}, cover_url = #{coverUrl}, audio_url = #{audioUrl}, release_time = #{releaseTime},
WHERE id = #{id}

mapper.select:
SELECT *
FROM tb_song
WHERE singer_id = #{singerId}
  AND name = #{songName}

SELECT * FROM tb_song
ORDER BY RAND()
LIMIT #{limit}
