mapper.update:
UPDATE tb_song
artist_id = #{artistId}, name = #{name}, album = #{album}, duration = #{duration}, style = #{style}, cover_path = #{coverPath}, audio_path = #{audioPath}, release_time = #{releaseTime}, play_count = #{playCount},
WHERE id = #{id}

mapper.select:
SELECT *
FROM tb_song
WHERE artist_id = #{artistId}
  AND name = #{songName}

SELECT * FROM tb_song
ORDER BY RAND()
LIMIT #{limit}
